#coding=utf-8from PyQt4 import QtGuiimport sysclass screen(QtGui.QDesktopWidget):    def __init__(self):        super(QtGui.QDesktopWidget,self).__init__()        self.t_width=self.width()#得到屏幕总宽度        self.t_height=self.height()#得到屏幕总高度        self.count=self.screenCount()#得到显示器的总数目        self.primaryIndex = self.primaryScreen()        self.screen=[]#屏幕列表            def splitscreen(self):                for i in range(self.count):                self.screen.append(self.screenGeometry(self.primaryIndex+i))#第i个屏幕的数据        return self.screen                  